import React, { Component } from 'react';

class CreateUserContainer extends Component {
    constructor(){
        super();
        this.state = {
            isClicked : false
        }
    }

    handleClick = () => {
        const {isClicked} = this.state
        if(isClicked === true){
            return 'signup-form show'
        } else {
            return "signup-form"
        }
    }

    render() {
        return (
            <div>
                <button>Click</button>
            </div>
        );
    }
}

export default CreateUserContainer;
